import React from 'react'

function WordItem( { word, remove} ) {
  return (
    <div className='word-item'>
        <p>English: {word.eng}</p>
        <p>На русском: {word.rus}</p>
        <button onClick={() => remove(word)}>Удалить</button>
    </div>
  )
}

export default WordItem
