import React, { useEffect, useState } from 'react';
import PhoneForm from './components/PhoneForm';
import PhoneList from './components/PhoneList';
import "./styles/App.css";

function App() {

  const [phones, setPhones] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const data = await fetch("https://localhost:44349/home/getphones");
      // convert data to json
      const json = await data.json();
      setPhones(json);
    }

  },[]);

  async function loadData() {
    const response = await fetch("https://localhost:44349/home/getphones");
    return await response.json();
  }

  function createPhone(phone) {
    const formData = new FormData();
    formData.append("model", phone.model);
    formData.append("price", phone.price);
    console.log("1");
    fetch("https://localhost:44349/home/createphone", {
      method: "post",
      body: formData
    })
    .then(response => {
      if(response.ok) {
        console.log("2");
        loadData();
      }
    }).catch(error => console.log(error));

    // setPhones([...phones, phone]);
  }

  function removePhone(phone) {
    const formData = new FormData();
    formData.append("model", phone.model);
    formData.append("price", phone.price);
    fetch("https://localhost:44349/home/removephone", {
      method: "post",
      body: formData
    })
    .then(response => {
      if(response.ok) {
        loadData();
      }
    }).catch(error => console.log(error));


  }

  return (
    <div className="App">
      <PhoneForm create={createPhone}/>
      <PhoneList phones={phones} remove={removePhone}/>
    </div>
  );
}

export default App;
