import React, { useState } from 'react';
import PhoneForm from './components/PhoneForm';
import PhoneList from './components/PhoneList';
import "./styles/App.css";

function App() {

  const [phones, setPhones] = useState([]);


  function componentDidMount() {
    this.loadData();
  }

  function loadData() {
    const phonesData = [];
    fetch("https://localhost:44349/home/getphones")
    .then(response => response.json())
    .then(data => {
      phonesData = [...data];
    }).catch(error => console.log(error))
    return phonesData;
  }

  function createPhone(phone) {
    const formData = new FormData();
    formData.append("model", phone.model);
    formData.append("price", phone.price);
    fetch("https://localhost:44349/home/createphones", {
      method: "post",
      body: formData
    })
    .then(response => {
      if(response.ok) {
        loadData();
      }
    }).catch(error => console.log(error));
  }

  function removePhone(phone) {
    const formData = new FormData();
    formData.append("model", phone.model);
    formData.append("price", phone.price);
    fetch("https://localhost:44349/home/removephone", {
      method: "post",
      body: formData
    })
    .then(response => {
      if(response.ok) {
        loadData();
      }
    }).catch(error => console.log(error));
  }

  return (
    <div className="App">
      <PhoneForm create={createPhone}/>
      <PhoneList phones={phones} remove={removePhone}/>
    </div>
  );
}

export default App;
